import React, { useState, useEffect } from 'react';
import {useParams} from "react-router-dom";
import NavbarIndex from '../../nav-bar/IndexNavbar';
import Http from '../../../http/apiService';
import { Button } from '@material-ui/core';
import TextField from '@material-ui/core/TextField';
const FileView =() =>{
    const {id} = useParams();
    const [data, setData] = useState([]);
    const GetFile = async () => {
        const url = '/files/getfile/'+id;
        const resp = await Http.getSth(url)
        setData(resp.data);
    }
    useEffect(() => {
        GetFile();
    }, []);
    
    const upComment=async ()=>{
        const url = '/comments/'+localStorage.getItem('username')+'/'+id;
        const resp = await Http.postSth(url,value)  
        getComment();
    }

    const getComment = async ()=>{
        const url = '/comments/'+id;
        const resp = await Http.getSth(url);
        console.log(resp);
    }

    const [value, setValue] = useState({
        content: '',

    });

    const handleOnChage = (event) => {
        setValue({
            ...value,
            [event.target.name]: event.target.value,
        })
    }
    return(
        <>
            <NavbarIndex/>
            <React.Fragment>                   
                            <p>File Name: {data.name}</p>
                            <p>User: {data.userName}</p>
                            <p>Comment: {data.comment}</p>
                            <React.Fragment>
                {data.map((element) => {
                    return (
                        <Grid item xs={4}>
                            <Paper className="box">
                                <p>File Name: {element.name}</p>
                                <p>User: {element.userName}</p>
                                <p>Comment: {element.comment}</p>
                                <Button className="btn-download" onClick={() => downLoad(element.id)}>DownLoad</Button>
                                <Button onClick={() => viewFile(element.id)}>View File</Button>
                            </Paper>
                        </Grid>
                    )
                }
                )
                }
            </React.Fragment>
                            
                            
                            <TextField id="outlined-basic" label="Comment" variant="outlined" onChange={handleOnChage} value={value.content} name="content" />

                            <Button onClick={upComment}>Comment</Button>                         
            </React.Fragment>

        </>
    )
}

export default FileView;
