import React, { useState, useEffect } from 'react';
import {useParams} from "react-router-dom";
import NavbarIndex from '../../nav-bar/IndexNavbar';
import Http from '../../../http/apiService';
import { Button } from '@material-ui/core';
import TextField from '@material-ui/core/TextField';
const FileView =() =>{
    const {id} = useParams();
    const [data, setData] = useState([]);
    const GetFile = async () => {
        const url = '/files/getfile/'+id;
        const resp = await Http.getSth(url)
        setData(resp.data);
    }
    useEffect(() => {
        GetFile();
    }, []);
    
    const upComment=async ()=>{
        const url = '/comment/'+localStorage.getItem('username')+'/'+id;
        const resp = await Http.postSth(url,value)  
    }

    
    const [value, setValue] = useState({
        comment: '',

    });

    const handleOnChage = (event) => {
        setValue({
            ...value,
            [event.target.name]: event.target.value,
        })
    }
    return(
        <>
            <NavbarIndex/>
            <React.Fragment>                   
                            <p>File Name: {data.name}</p>
                            <p>User: {data.userName}</p>
                            <p>Comment: {data.comment}</p>

                            <TextField id="outlined-basic" label="Commnent" variant="outlined" onChange={handleOnChage} value={value.comment} name="comment" />

                            <Button onClick={upComment}>Comment</Button>                         
            </React.Fragment>

        </>
    )
}

export default FileView;
