import React, { useState, useEffect } from 'react';
import Http from '../../../http/apiService';
import { TextField, MenuItem, Select, InputLabel } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import FormControl from '@material-ui/core/FormControl';
import { Button } from '@material-ui/core';
import { useHistory } from "react-router-dom";


const UpdateFileIndex = () => {


    const [data, setData] = useState();
    const [infoFile, setInfoFile] = useuseState();

    const GetFile = async () => {
        const currentHref = window.location.href.split('/');
        const id = currentHref[currentHref.length - 1];
        const url = '/files/getfile/' + id;
        const resp = await Http.getSth(url)
        setData(resp.data);
        console.log(resp.data)
    }   
    
    const handleChange = (event) => {
        setData({...infoFile, [event.target.name]: event.target.value, [event.target.name]: event.target.value});
    }

    useEffect(() => {
        GetFile();
    }, []);
    return(
        <>
            <div>
                {data && <TextField  id="standard-basic" label="File Name" disabled defaultValue={data?.name} />}
            </div>
            <div>
                {data && <TextField  id="standard-basic" label="Category" onChange={handleChange} defaultValue={data?.category}/>}
            </div>
            <div>
                {data && <TextField  id="standard-basic" label="Comment" onChange={handleOnChage} defaultValue={data?.comment} />}
            </div>
            <Button >Update</Button>
        </>
    )
}



export default UpdateFileIndex;
