import React, { useState, useEffect } from 'react';
import Http from '../../../http/apiService';
import {TextField, MenuItem, Select} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';


const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
}));
const UpdateUserIndex = () => {
const classes = useStyles();

const [data, setData] = useState();
    const GetUserInfo = async () => {
        const currentHref = window.location.href.split('/');
        const name =currentHref[currentHref.length - 1];
        const url = '/users/' + name;
        const resp = await Http.getSth(url)
        setData(resp.data);
    }

    useEffect(() => {
        GetUserInfo();
    }, []);
     return (
        <React.Fragment>
            {data && <TextField id="standard-basic" label="User Name" defaultValue={`${data.userName}`} disabled />}
            <Select
                labelId="demo-simple-select-label"
                id="demo-simple-select">

            <MenuItem >ROLE_USER</MenuItem>
            <MenuItem >ROLE_ADMIN</MenuItem>
            </Select>
        </React.Fragment>
    );
}

export default UpdateUserIndex;
